import page.menu as menu
import digital_signature as ds
import easygui
import start

def init(top, gui, *args, **kwargs):
    global w, top_level, root
    w = gui
    top_level = top
    root = top

def destroy_window():
    # Function which closes the window.
    global top_level
    top_level.destroy()
    top_level = None

def backToMenuWindow():
    destroy_window()
    menu.vp_start_gui()

def openFile(dsPath = False):
    return easygui.fileopenbox(title = "Select file " + "for the separated signature" if dsPath else ""  + " (.txt)")

def tapInsert():
    path = openFile()
    if (path != None):
        if (start.SorV == "S"):
            f = open('./key/rsa.pri', 'r')
            S = ds.make_digital_signature(path, int(float(f.read().split(" ")[0])), 223427)
            ds.add_digital_signature(S, path)
            f.close()
            start.status = "Status: Document succesfully signed to " + path.split('\\')[-1]
        elif (start.SorV == "V"):
            f = open('./key/rsa.pub', 'r')
            start.status = "Status: " + ds.verification_ds(path, path, int(f.read().split(" ")[0]), 223427, "Yes")
            f.close()
        else:
            return
        backToMenuWindow()

def tapSeparate():
    path = openFile()
    if (path != None):
        if (start.SorV == "S"):
            f = open('./key/rsa.pri', 'r')
            S = ds.make_digital_signature(path, int(float(f.read().split(" ")[0])), 223427)
            dsPath = openFile(dsPath = True)
            ds.add_digital_signature(S, dsPath)
            f.close()
            start.status = "Status: Document succesfully signed to " + path.split('\\')[-1]
        elif (start.SorV == "V"):
            f = open('./key/rsa.pub', 'r')
            dsPath = openFile(dsPath = True)
            start.status = "Status: " + ds.verification_ds(path, dsPath, int(f.read().split(" ")[0]), 223427, "No")
            f.close()
        else:
            return
        backToMenuWindow()